import React from "react";
import "./style.css";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardSubtitle,
  Button,
  CardFooter
} from "reactstrap";

const Product = ({ item: { title, price, description, img } }) => {
  return (
    <div className="product">
      <Card>
        <CardImg top width="100%" alt="product" src={img} />
        <CardBody>
          <CardText className="title">{title}</CardText>
          <CardText>
            {description < 1000
              ? description
              : `${description.substring(0, 25)}...`}
          </CardText>
          <CardFooter>
            <Button color="info">Add to Cart</Button>
            <CardText className="price">${price}</CardText>
          </CardFooter>
        </CardBody>
      </Card>
    </div>
  );
};

export default Product;
