import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import App from "./App";
import * as serviceWorker from "./serviceWorker";
import { createStore, applyMiddleware, compose } from "redux";
import rootReducer from "./store/reducers/rootReducer";
import { Provider } from "react-redux";
import thunk from "redux-thunk";
import {
  createFirestoreInstance,
  getFirestore,
  reduxFirestore
} from "redux-firestore";
import { ReactReduxFirebaseProvider, getFirebase } from "react-redux-firebase";
import fbConfig from "./config/fbConfig";
import firebase from "firebase/app";

const store = createStore(
  rootReducer,
  compose(
    applyMiddleware(thunk.withExtraArgument({ getFirestore, getFirebase })),
    reduxFirestore(firebase, fbConfig)
  )
);

const rrfProps = {
  firebase,
  config: fbConfig,
  dispatch: store.dispatch,
  createFirestoreInstance
};

ReactDOM.render(
  <Provider store={store}>
    <ReactReduxFirebaseProvider {...rrfProps}>
      <App />
    </ReactReduxFirebaseProvider>
  </Provider>,
  document.getElementById("root")
);

serviceWorker.unregister();

/* rootReducers combines all of our reducers, we pass it to the store
  Provider surrounds our app and passes the Store into the application
  it binds redux with our react app

    we appled thunk middleware to enhance our Store with extra funcionality 

   we will use function withExtraArgument on Thunk. our extra argument will be an object in which we can pass our properies-getFirestore, getFirebase. so no we can use this object with properties 'getFirestore', 'getFirebase' in projectActions as extra argument in our function (thunk allows us to return  a function)

   we are using compose to add multiple store enhancers (our store enhancers are reduxFireStore(), reactReduxFirebase() and applyMiddleware(thunk)(...). Similar to root reduers, where we combine all of our reducers. 
   we need to pass our config file to reduxFireStore(),
   reactReduxFirebase() so they know where to conntect to
