import { items } from "../../data";
import * as itemActions from "./items.types";
import { createStore } from "redux";

export const itemsReducer = (state = { items: items, ed: 2 }, action) => {
  switch (action.type) {
    case itemActions.ADD_ITEMS:
      return { ...state, items: [...state.items, action.payLoad] };
    default:
      return state;
  }
};

export const store = createStore(itemsReducer);
