import authReducer from "./authReducer";
import projectReducer from "./projectReducer";
import { combineReducers } from "redux";
import { firestoreReducer } from "redux-firestore";
import { firebaseReducer } from "react-redux-firebase";

const rootReducer = combineReducers({
  // auth: authReducer,
  // project: projectReducer,
  firestore: firestoreReducer,
  firebase: firebaseReducer
});

export default rootReducer;

/* create a conts that will use combineReducers function, give them names and then  pass it to index.js to function 'create store' that is inside of const store 
   
   firestoreReducer is made to sync our firestore data with our state
   firebase does the same thing but with authentication
   */
